function(index, item) {
  NeutrinoConfig.PS2LOGO = (index == 1);
  let config = CfgMan.Get("neutrino.cfg");
  config["logo"] = NeutrinoConfig.PS2LOGO.toString();
  CfgMan.Push("neutrino.cfg", config);
}


