function(index, item) {
  let cheats = []; cheats.push({ code: "480p", enabled: (index === 1) });
  setPOPSCheat({ cheats: cheats });
  if (UserConfig.HDD) setPOPSCheat({ cheats: cheats, device:"hdd" });
}


