function(index, item) {
  const cheats = [];
  cheats.push({ code: "IGR0", enabled: (index === 1) });
  cheats.push({ code: "IGR1", enabled: (index === 2) });
  cheats.push({ code: "IGR2", enabled: (index === 3) });
  cheats.push({ code: "IGR3", enabled: (index === 4) });
  cheats.push({ code: "IGR4", enabled: (index === 5) });
  cheats.push({ code: "IGR5", enabled: (index === 6) });
  cheats.push({ code: "NOIGR", enabled: (index === 7) });
  setPOPSCheat({ cheats: cheats });
  if (UserConfig.HDD) setPOPSCheat({ cheats: cheats, device:"hdd" });
}


