Dialog.Confirm:

  	  function() {
		  UserConfig.ParentalCode = DashUI.Dialog.Data[DashUI.Dialog.Level].TmpCode;
		  let config = CfgMan.Get("main.cfg");
		  config["prntcode"] = `[ ${UserConfig.ParentalCode[0].toString()}, ${UserConfig.ParentalCode[1].toString()}, ${UserConfig.ParentalCode[2].toString()}, ${UserConfig.ParentalCode[3].toString()} ]`;
		  CfgMan.Push("main.cfg", config);
		  UIAnimationDialogFade_Start(false);
	  }

Option.Confirm:

function(index, item) {
	UserConfig.ParentalSet = index;
	let config = CfgMan.Get("main.cfg");
	config["parental"] = index.toString();
	CfgMan.Push("main.cfg", config);
}
